const contarCoincidencias=()=>{const e=document.getElementById("regex").value,t=document.getElementById("text").value;try{const n=new RegExp(e,"g"),c=t.match(n),o=c?c.length:0;document.getElementById("result").textContent=`Resultado: ${o} coincidencia${1!==o?"s":""}`}catch(e){document.getElementById("result").textContent="Expresión regular no válida"}};